const HOST = pw.env.get("HOST");
const UNSET_ENV = pw.env.get("UNSET_ENV");
const EXPECTED_URL = "https://echo.hoppscotch.io";
const URL = pw.env.get("URL");
const BODY_VALUE = pw.env.get("BODY_VALUE");

// Check JSON response property
pw.test("Check headers properties.", ()=> {
    pw.expect(pw.response.body.headers.host).toBe(HOST);
});

pw.test("Check data properties.", () => {
	const DATA = pw.response.body.data;

    pw.expect(DATA).toBeType("string");
    pw.expect(JSON.parse(DATA).body_key).toBe(BODY_VALUE);
});

pw.test("Check request URL.", () => {
  pw.expect(URL).toBe(EXPECTED_URL);
})

pw.test("Check unset ENV.", () => {
  pw.expect(UNSET_ENV).toBeType("undefined");
})
