requests.testScript:
pw.test("Accounts for active request variables", () => {
  pw.expect(pw.response.body.args["query-param-key"]).toBe("query-param-value");
  const data = JSON.parse(pw.response.body.data);
  pw.expect(data["http-body-raw-key"]).toBe("http-body-raw-value");
  pw.expect(pw.response.body.headers["custom-header-key"]).toBe("custom-header-value");
});

pw.test("Ignores inactive request variables", () => {
  pw.expect(pw.response.body.args["inactive-query-param-key"]).toBe("");
  pw.expect(pw.response.body.headers["inactive-header-key"]).toBe(undefined);
});

pw.test("The first occurrence is picked for multiple request variable occurrences with the same key.", () => {
  pw.expect(pw.response.body.args.method).toBe("post");
});

pw.test("Request variables support recursive resolution and pick values from secret environment variables", () => {
  const { username, password } = JSON.parse(pw.response.body.data);
  pw.expect(username).toBe("username");
  pw.expect(password).toBe("password");
});

pw.test("Resolves request variables that are clubbed together", () => {
  pw.expect(pw.response.body.path).toBe("/username/password");
});

pw.test("Request variables are prioritised over environment variables", () => {
  pw.expect(pw.response.body.headers.host).toBe("echo.hoppscotch.io");
});

pw.test("Environment variable is picked if the request variable under the same name is empty", () => {
  pw.expect(pw.response.body.headers["test-header-key"]).toBe("test-header-value");
});
